var http = require("http");
var router = require("router");

var server = {
	connect: function() {
		http.createServer(function(request, response) {
			router.route.init(request, response);
		}).listen(8888);
		console.log('Is running ...')
	}
};

if (module.parent == null) {
	server.connect();
} else {
	module.exports = server;
}
