var route = {
	showHead: function(response,code) {
		response.writeHead(code,{"Content-Type":"text/plain"})
	},
	write: function(response, data) {
		response.write(data);
	},
	end: function(response){
		response.end();
	},
	init: function(request,response){
		console.log("rout init", request, response);
		this.showHead(response,200);
		this.write(response,"Привет мир!");
		this.end(response);

	}
}

module.exports.route = route;